    public static final String[] words = main.split(" ");

    public static String randomWord() {
        return words[4];
    }

    public static String randomWord(int level) {
        String str = words[random.nextInt(words.length)];
        if(level == 10 ){
            return str;
        }
        if((str.length()-1)== level) {
            return str;
        }
        return randomWord(level);
    }

    public static String shuffleWords(String word) {
        List<Character> characters = new ArrayList<Character>();
        for(char c:word.toCharArray()){
            characters.add(c);
        }
        StringBuilder output = new StringBuilder(word.length());
        while(characters.size()!=0){
            int randPicker = (int)(Math.random()*characters.size());
            output.append(characters.remove(randPicker));
        }
        return output.toString();
    }
